import { useState, useEffect } from 'react'
import { useLocation } from 'react-router-dom'

import Container from '../layout/Container'
import Loading from '../layout/Loading'

import LinkButton from '../layout/LinkButton'
import LocalCard from '../local/LocalCard'
import Message from '../layout/Message'

import styles from './Locais.module.css'

function Locais() {
  const [locais, setLocais] = useState([])
  const [removeLoading, setRemoveLoading] = useState(false)
  const [localMessage, setLocalMessage] = useState('')

  const location = useLocation()
  let message = ''
  if (location.state) {
    message = location.state.message
  }

  useEffect(() => {
    // Para ver o loading
    setTimeout(
      () =>
        fetch('http://https://localhost:7193/api/cliente/', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        })
          .then((resp) => resp.json())
          .then((data) => {
            setLocais(data)
            setRemoveLoading(true)
          }),
      100,
    )
  }, [])

  function removeLocal(id) {
    fetch(`http://localhost:5000/locais/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((resp) => resp.json())
      .then((data) => {
        setLocais(locais.filter((local) => local.id !== id))
        setLocalMessage('Local removido com sucesso!')
      })
  }

  return (
    <div className={styles.local_container}>
        <Locais/>
    </div>
  )
}

export default Locais
